import test from 'japa'

import Jwt from '../src/Jwt'
import Token from '../src/Token'
import { mockConfig, mockAxiosInstance } from './setup.spec'

test.group('Jwt.verifyOffline', () => {
  const invalidAccessToken = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3ODkwIiwibmFtZSI6IkpvaG4gRG9lIiwiaWF0IjoxNTE2MjM5MDIyfQ.SflKxwRJSMeKKF2QT4fwpMeJf36POk6yJV_adQssw5cA'
  const validAccessToken = 'eyJhbGciOiJQUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3ODkwIiwibmFtZSI6IkpvaG4gRG9lIiwiYWRtaW4iOnRydWUsImlhdCI6MTUxNjIzOTAyMn0.iOeNU4dAFFeBwNj6qdhdvm-IvDQrTa6R22lQVJVuWJxorJfeQww5Nwsra0PjaOYhAMj9jNMO5YLmud8U7iQ5gJK2zYyepeSuXhfSi8yjFZfRiSkelqSkU19I-Ja8aQBDbqXf2SAWA8mHF8VS3F08rgEaLCyv98fLLH4vSvsJGf6ueZSLKDVXz24rZRXGWtYYk_OYYTVgR1cg0BLCsuCvqZvHleImJKiWmtS0-CymMO4MMjCy_FIl6I56NqLE9C87tUVpo1mT-kbg5cHDD8I7MjCW5Iii5dethB4Vid3mZ6emKjVYgXrtkOQ-JyGMh6fnQxEFN1ft33GX2eRHluK9eg'
  const cert = '-----BEGIN PUBLIC KEY-----\\n-----END PUBLIC KEY-----'.replace(/\\n/g, '\n')
  const jwt = new Jwt(mockConfig, mockAxiosInstance)

  test('when the access token is invalid it should throw an error', async (assert) => {
    const expectedErrorMessage = 'invalid algorithm'

    try {
      await jwt.verifyOffline(invalidAccessToken, cert)
    } catch ({ message }) {
      assert.equal(message, expectedErrorMessage)
    }
  })

  test('when the access token is valid it should return a new Token instance', async (assert) => {
    const result = await jwt.verifyOffline(validAccessToken, cert, { algorithms: ['PS256'] })

    assert.instanceOf(result, Token)
  })
})
